import{_ as e,c as t,o as r,a}from"./app.8ed03e25.js";var i="/assets/bundler.37740380.png",o="/assets/esm.3070012d.png";const _=JSON.parse('{"title":"Vite\uB97C \uC0AC\uC6A9\uD574\uC57C \uD558\uB294 \uC774\uC720","description":"","frontmatter":{"title":"Vite\uB97C \uC0AC\uC6A9\uD574\uC57C \uD558\uB294 \uC774\uC720"},"headers":[{"level":2,"title":"\uC774\uB7F0 \uBB38\uC81C\uC810\uC774 \uC788\uC5C8\uC5B4\uC694","slug":"the-problems"},{"level":3,"title":"\uC9C0\uB8E8\uD560 \uC815\uB3C4\uB85C \uAE38\uC5C8\uB358 \uC11C\uBC84 \uAD6C\uB3D9","slug":"slow-server-start"},{"level":3,"title":"\uB290\uB838\uB358 \uC18C\uC2A4 \uCF54\uB4DC \uAC31\uC2E0","slug":"slow-updates"},{"level":2,"title":"\uBC30\uD3EC \uC2DC \uBC88\uB4E4\uB9C1 \uACFC\uC815\uC774 \uD544\uC694\uD55C \uC774\uC720","slug":"why-bundle-for-production"},{"level":2,"title":"\uC65C \uBC88\uB4E4\uB9C1 \uC2DC\uC5D0\uB294 Esbuild\uB97C \uC0AC\uC6A9\uD558\uC9C0 \uC54A\uB098\uC694?","slug":"why-not-bundle-with-esbuild"},{"level":2,"title":"Vite\uC640 \uB2E4\uB978 \uB3C4\uAD6C\uC758 \uCC28\uC774\uC810","slug":"how-is-vite-different-from-x"}],"relativePath":"guide/why.md"}'),l={name:"guide/why.md"},s=a('<h1 id="why-vite" tabindex="-1">Vite\uB97C \uC0AC\uC6A9\uD574\uC57C \uD558\uB294 \uC774\uC720 <a class="header-anchor" href="#why-vite" aria-hidden="true">#</a></h1><h2 id="the-problems" tabindex="-1">\uC774\uB7F0 \uBB38\uC81C\uC810\uC774 \uC788\uC5C8\uC5B4\uC694 <a class="header-anchor" href="#the-problems" aria-hidden="true">#</a></h2><p>\uBE0C\uB77C\uC6B0\uC800\uC5D0\uC11C ESM(ES Modules)\uC744 \uC9C0\uC6D0\uD558\uAE30 \uC804\uAE4C\uC9C0\uB294, JavaScript \uBAA8\uB4C8\uD654\uB97C \uB124\uC774\uD2F0\uBE0C \uB808\uBCA8\uC5D0\uC11C \uC9C4\uD589\uD560 \uC218 \uC5C6\uC5C8\uC2B5\uB2C8\uB2E4. \uB530\uB77C\uC11C \uAC1C\uBC1C\uC790\uB4E4\uC740 &quot;\uBC88\uB4E4\uB9C1(Bundling)*&quot;\uC774\uB77C\uB294 \uC6B0\uD68C\uC801\uC778 \uBC29\uBC95\uC744 \uC0AC\uC6A9\uD560 \uC218 \uBC16\uC5D0 \uC5C6\uC5C8\uC8E0. (* \uBC88\uB4E4\uB9C1: \uBAA8\uB4C8\uD654\uB41C \uC18C\uC2A4 \uCF54\uB4DC\uB97C \uBE0C\uB77C\uC6B0\uC800\uC5D0\uC11C \uC2E4\uD589\uD560 \uC218 \uC788\uB294 \uD30C\uC77C\uB85C \uD55C\uB370 \uBB36\uC5B4 \uC5F0\uACB0\uD574\uC8FC\uB294 \uC791\uC5C5)</p><p><a href="https://webpack.js.org/" target="_blank" rel="noopener noreferrer">Webpack</a>, <a href="https://rollupjs.org" target="_blank" rel="noopener noreferrer">Rollup</a> \uADF8\uB9AC\uACE0 <a href="https://parceljs.org/" target="_blank" rel="noopener noreferrer">Parcel</a>\uACFC \uAC19\uC740 \uB3C4\uAD6C\uB294 \uC774\uB7F0 \uBC88\uB4E4\uB9C1 \uC791\uC5C5\uC744 \uC9C4\uD589\uD574\uC90C\uC73C\uB85C\uC368 \uD504\uB7F0\uD2B8\uC5D4\uB4DC \uAC1C\uBC1C\uC790\uC758 \uC0DD\uC0B0\uC131\uC744 \uD06C\uAC8C \uD5A5\uC0C1\uC2DC\uCF30\uC2B5\uB2C8\uB2E4.</p><p>\uD558\uC9C0\uB9CC \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uC810\uC810 \uB354 \uBC1C\uC804\uD568\uC5D0 \uB530\uB77C \uCC98\uB9AC\uD574\uC57C \uD558\uB294 JavaScript \uBAA8\uB4C8\uC758 \uAC1C\uC218\uB3C4 \uADF9\uC801\uC73C\uB85C \uC99D\uAC00\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uC2EC\uC9C0\uC5B4 \uC218\uCC9C \uAC1C\uC758 \uBAA8\uB4C8\uC774 \uC874\uC7AC\uD558\uB294 \uAC83\uB3C4 \uB300\uADDC\uBAA8 \uD504\uB85C\uC81D\uD2B8\uC5D0\uC11C\uB294 \uADF8\uB9AC \uB4DC\uBB38 \uC77C\uC774 \uC544\uB2D9\uB2C8\uB2E4. \uC774\uB7EC\uD55C \uC0C1\uD669\uC5D0\uC11C JavaScript \uAE30\uBC18\uC758 \uB3C4\uAD6C\uB294 \uC131\uB2A5 \uBCD1\uBAA9 \uD604\uC0C1\uC774 \uBC1C\uC0DD\uB418\uC5C8\uACE0, \uC885\uC885 \uAC1C\uBC1C \uC11C\uBC84\uB97C \uAC00\uB3D9\uD558\uB294 \uB370 \uBE44\uD569\uB9AC\uC801\uC73C\uB85C \uC624\uB79C \uC2DC\uAC04\uC744 \uAE30\uB2E4\uB824\uC57C \uD55C\uB2E4\uAC70\uB098 HMR\uC744 \uC0AC\uC6A9\uD558\uB354\uB77C\uB3C4 \uBCC0\uACBD\uB41C \uD30C\uC77C\uC774 \uC801\uC6A9\uB420 \uB54C \uAE4C\uC9C0 \uC218 \uCD08 \uC774\uC0C1 \uC18C\uC694\uB418\uACE4 \uD588\uC2B5\uB2C8\uB2E4. \uC774\uC640 \uAC19\uC740 \uB290\uB9B0 \uD53C\uB4DC\uBC31 \uB8E8\uD504\uB294 \uAC1C\uBC1C\uC790\uC758 \uC0DD\uC0B0\uC131\uACFC \uD589\uBCF5\uC5D0 \uC801\uC9C0 \uC54A\uC740 \uC601\uD5A5\uC744 \uC904 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><p>Vite\uB294 \uC774\uB7EC\uD55C \uAC83\uC5D0 \uCD08\uC810\uC744 \uB9DE\uCDB0, \uBE0C\uB77C\uC6B0\uC800\uC5D0\uC11C \uC9C0\uC6D0\uD558\uB294 ES Modules(ESM) \uBC0F \uB124\uC774\uD2F0\uBE0C \uC5B8\uC5B4\uB85C \uC791\uC131\uB41C JavaScript \uB3C4\uAD6C \uB4F1\uC744 \uD65C\uC6A9\uD574 \uBB38\uC81C\uB97C \uD574\uACB0\uD558\uACE0\uC790 \uD569\uB2C8\uB2E4.</p><h3 id="slow-server-start" tabindex="-1">\uC9C0\uB8E8\uD560 \uC815\uB3C4\uB85C \uAE38\uC5C8\uB358 \uC11C\uBC84 \uAD6C\uB3D9 <a class="header-anchor" href="#slow-server-start" aria-hidden="true">#</a></h3><p>\uCF5C\uB4DC-\uC2A4\uD0C0\uD2B8* \uBC29\uC2DD\uC73C\uB85C \uAC1C\uBC1C \uC11C\uBC84\uB97C \uAD6C\uB3D9\uD560 \uB54C, \uBC88\uB4E4\uB7EC \uAE30\uBC18\uC758 \uB3C4\uAD6C\uC758 \uACBD\uC6B0 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uB0B4 \uBAA8\uB4E0 \uC18C\uC2A4 \uCF54\uB4DC\uC5D0 \uB300\uD574 \uD06C\uB864\uB9C1 \uBC0F \uBE4C\uB4DC \uC791\uC5C5\uC744 \uB9C8\uCCD0\uC57C\uC9C0\uB9CC\uC774 \uC2E4\uC81C \uD398\uC774\uC9C0\uB97C \uC81C\uACF5\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. (* \uCF5C\uB4DC-\uC2A4\uD0C0\uD2B8: \uCD5C\uCD08\uB85C \uC2E4\uD589\uB418\uC5B4 \uC774\uC804\uC5D0 \uCE90\uC2F1\uD55C \uB370\uC774\uD130\uAC00 \uC5C6\uB294 \uACBD\uC6B0\uB97C \uC758\uBBF8)</p><p>vite\uB294 \uC774 \uBB38\uC81C\uB97C <strong>dependencies</strong> \uADF8\uB9AC\uACE0 <strong>source code</strong> \uB450 \uAC00\uC9C0 \uCE74\uD14C\uACE0\uB9AC\uB85C \uB098\uB204\uC5B4 \uAC1C\uBC1C \uC11C\uBC84\uB97C \uC2DC\uC791\uD558\uB3C4\uB85D \uD568\uC73C\uB85C\uC368 \uD574\uACB0\uD588\uC8E0.</p><ul><li><p><strong>Dependencies</strong>: \uAC1C\uBC1C \uC2DC \uADF8 \uB0B4\uC6A9\uC774 \uBC14\uB00C\uC9C0 \uC54A\uC744 \uC77C\uBC18\uC801\uC778(Plain) JavaScript \uC18C\uC2A4 \uCF54\uB4DC\uC785\uB2C8\uB2E4. \uAE30\uC874 \uBC88\uB4E4\uB7EC\uB85C\uB294 \uCEF4\uD3EC\uB10C\uD2B8 \uB77C\uC774\uBE0C\uB7EC\uB9AC\uC640 \uAC19\uC774 \uBA87 \uBC31 \uAC1C\uC758 JavaScript \uBAA8\uB4C8\uC744 \uAC16\uACE0 \uC788\uB294 \uB9E4\uC6B0 \uD070 \uB514\uD39C\uB358\uC2DC\uC5D0 \uB300\uD55C \uBC88\uB4E4\uB9C1 \uACFC\uC815\uC774 \uB9E4\uC6B0 \uBE44\uD6A8\uC728\uC801\uC774\uC5C8\uACE0 \uB9CE\uC740 \uC2DC\uAC04\uC744 \uD544\uC694\uB85C \uD588\uC2B5\uB2C8\uB2E4.</p><p>Vite\uC758 <a href="./dep-pre-bundling.html">\uC0AC\uC804 \uBC88\uB4E4\uB9C1</a> \uAE30\uB2A5\uC740 <a href="https://esbuild.github.io/" target="_blank" rel="noopener noreferrer">Esbuild</a>\uB97C \uC0AC\uC6A9\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. Go\uB85C \uC791\uC131\uB41C Esbuild\uB294 Webpack, Parcel\uACFC \uAC19\uC740 \uAE30\uC874\uC758 \uBC88\uB4E4\uB7EC \uB300\uBE44 10-100\uBC30 \uBE60\uB978 \uBC88\uB4E4\uB9C1 \uC18D\uB3C4\uB97C \uBCF4\uC600\uC8E0.</p></li><li><p><strong>Source code</strong>: JSX, CSS \uB610\uB294 Vue/Svelte \uCEF4\uD3EC\uB10C\uD2B8\uC640 \uAC19\uC774 \uCEF4\uD30C\uC77C\uB9C1\uC774 \uD544\uC694\uD558\uACE0, \uC218\uC815 \uB610\uD55C \uB9E4\uC6B0 \uC7A6\uC740 Non-plain JavaScript \uC18C\uC2A4 \uCF54\uB4DC\uB294 \uC5B4\uB5BB\uAC8C \uD560\uAE4C\uC694? (\uBB3C\uB860 \uC774\uB4E4 \uC5ED\uC2DC \uD2B9\uC815 \uC2DC\uC810\uC5D0\uC11C \uBAA8\uB450 \uBD88\uB7EC\uC62C \uD544\uC694\uB294 \uC5C6\uC2B5\uB2C8\uB2E4.)</p><p>vite\uB294 <a href="https://developer.mozilla.org/en-US/docs/Web/JavaScript/Guide/Modules" target="_blank" rel="noopener noreferrer">Native ESM</a>\uC744 \uC774\uC6A9\uD574 \uC18C\uC2A4 \uCF54\uB4DC\uB97C \uC81C\uACF5\uD558\uB3C4\uB85D \uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uB2E4\uC2DC\uB9D0\uD574, \uBE0C\uB77C\uC6B0\uC800\uAC00 \uACE7 \uBC88\uB4E4\uB7EC\uB77C\uB294 \uB9D0\uC774\uC8E0. vite\uB294 \uADF8\uC800 \uBE0C\uB77C\uC6B0\uC800\uC758 \uD310\uB2E8 \uC544\uB798 \uD2B9\uC815 \uBAA8\uB4C8\uC5D0 \uB300\uD55C \uC18C\uC2A4 \uCF54\uB4DC\uB97C \uC694\uCCAD\uD558\uBA74 \uC774\uB97C \uC804\uB2EC\uD560 \uBFD0\uC785\uB2C8\uB2E4. \uB530\uB77C\uC11C \uC870\uAC74\uBD80 \uB3D9\uC801 import \uC774\uD6C4\uC758 \uCF54\uB4DC\uB294 \uD604\uC7AC \uD654\uBA74\uC5D0\uC11C \uC2E4\uC81C\uB85C \uC0AC\uC6A9\uC774 \uB418\uC5B4\uC57C\uB9CC \uCC98\uB9AC\uAC00 \uB429\uB2C8\uB2E4.</p><p><img src="'+i+'" alt="\uBC88\uB4E4\uB7EC \uAE30\uBC18\uC758 \uAC1C\uBC1C \uC11C\uBC84"></p><p><img src="'+o+'" alt="ESM \uAE30\uBC18\uC758 \uAC1C\uBC1C \uC11C\uBC84"></p></li></ul><h3 id="slow-updates" tabindex="-1">\uB290\uB838\uB358 \uC18C\uC2A4 \uCF54\uB4DC \uAC31\uC2E0 <a class="header-anchor" href="#slow-updates" aria-hidden="true">#</a></h3><p>\uAE30\uC874\uC758 \uBC88\uB4E4\uB7EC \uAE30\uBC18\uC73C\uB85C \uAC1C\uBC1C\uC744 \uC9C4\uD589\uD560 \uB54C, \uC18C\uC2A4 \uCF54\uB4DC\uB97C \uC5C5\uB370\uC774\uD2B8 \uD558\uAC8C \uB418\uBA74 \uBC88\uB4E4\uB9C1 \uACFC\uC815\uC744 \uB2E4\uC2DC \uAC70\uCCD0\uC57C \uD588\uC5C8\uC2B5\uB2C8\uB2E4. \uB530\uB77C\uC11C \uC11C\uBE44\uC2A4\uAC00 \uCEE4\uC9C8\uC218\uB85D \uC18C\uC2A4 \uCF54\uB4DC \uAC31\uC2E0 \uC2DC\uAC04 \uB610\uD55C \uC120\uD615\uC801\uC73C\uB85C \uC99D\uAC00\uD560 \uC218 \uBC16\uC5D0 \uC5C6\uC5C8\uC8E0.</p><p>\uC77C\uBD80 \uBC88\uB4E4\uB7EC\uC758 \uACBD\uC6B0 \uBA54\uBAA8\uB9AC \uC0C1\uC5D0\uC11C \uC774\uB97C \uC9C4\uD589\uD558\uC5EC \uC2E4\uC81C\uB85C \uAC31\uC2E0\uC5D0 \uC601\uD5A5\uC744 \uBC1B\uB294 \uD30C\uC77C\uB4E4\uB9CC\uC744 \uC0C8\uB85C\uC774 \uBC88\uB4E4\uB9C1\uD558\uAC8C\uB054 \uD558\uC600\uC73C\uB098, \uACB0\uAD6D \uC774 \uC5ED\uC2DC \uCC98\uC74C\uC5D0\uB294 \uBAA8\uB4E0 \uD30C\uC77C\uC5D0 \uB300\uD55C \uBC88\uB4E4\uB9C1\uC744 \uC9C4\uD589\uD574\uC57C \uD588\uC5C8\uC8E0. &quot;\uBAA8\uB4E0 \uD30C\uC77C&quot;\uC744 \uBC88\uB4E4\uB9C1 \uD558\uACE0, \uC774\uB97C \uB2E4\uC2DC \uC6F9 \uD398\uC774\uC9C0\uC5D0\uC11C \uBD88\uB7EC\uC624\uB294 \uAC83\uC774 \uC5BC\uB9C8\uB098 \uBE44\uD6A8\uC728\uC801\uC778 \uAC83\uC778\uC9C0 \uB290\uAEF4\uC9C0\uC2DC\uB098\uC694? \uC774\uB7EC\uD55C \uC774\uC288\uB97C \uC6B0\uD68C\uD558\uACE0\uC790 HMR(Hot Module Replacement)* \uC774\uB77C\uB294 \uB300\uC548\uC774 \uB098\uC654\uC73C\uB098, \uC774 \uC5ED\uC2DC \uBA85\uD655\uD55C \uD574\uB2F5\uC740 \uC544\uB2C8\uC5C8\uC2B5\uB2C8\uB2E4. (* HMR: \uC571\uC744 \uC885\uB8CC\uD558\uC9C0 \uC54A\uACE0 \uAC31\uC2E0\uB41C \uD30C\uC77C\uB9CC\uC744 \uAD50\uCCB4\uD558\uB294(Replacement) \uBC29\uC2DD. \uB2E4\uB9CC \uB9C8\uCC2C\uAC00\uC9C0\uB85C \uC571 \uC0AC\uC774\uC988\uAC00 \uCEE4\uC9C8\uC218\uB85D \uC120\uD615\uC801\uC73C\uB85C \uAC31\uC2E0\uC5D0 \uD544\uC694\uD55C \uC2DC\uAC04\uC774 \uC99D\uAC00\uD55C\uB2E4.)</p><p>\uBB3C\uB860, vite\uB294 HMR\uC744 \uC9C0\uC6D0\uD569\uB2C8\uB2E4. \uBC88\uB4E4\uB7EC\uAC00 \uC544\uB2CC ESM\uC744 \uC774\uC6A9\uD574\uC11C \uB9D0\uC774\uC8E0. \uC5B4\uB5A4 \uBAA8\uB4C8\uC774 \uC218\uC815\uB418\uBA74 vite\uB294 \uADF8\uC800 \uC218\uC815\uB41C \uBAA8\uB4C8\uACFC \uAD00\uB828\uB41C \uBD80\uBD84\uB9CC\uC744 \uAD50\uCCB4\uD560 \uBFD0\uC774\uACE0, \uBE0C\uB77C\uC6B0\uC800\uC5D0\uC11C \uD574\uB2F9 \uBAA8\uB4C8\uC744 \uC694\uCCAD\uD558\uBA74 \uAD50\uCCB4\uB41C \uBAA8\uB4C8\uC744 \uC804\uB2EC\uD560 \uBFD0\uC785\uB2C8\uB2E4. \uC804 \uACFC\uC815\uC5D0\uC11C \uC644\uBCBD\uD558\uAC8C ESM\uC744 \uC774\uC6A9\uD558\uAE30\uC5D0, \uC571 \uC0AC\uC774\uC988\uAC00 \uCEE4\uC838\uB3C4 HMR\uC744 \uD3EC\uD568\uD55C \uAC31\uC2E0 \uC2DC\uAC04\uC5D0\uB294 \uC601\uD5A5\uC744 \uB07C\uCE58\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.</p><p>\uB610\uD55C vite\uB294 HTTP \uD5E4\uB354\uB97C \uC774\uC6A9\uD574 \uD37C\uD3EC\uBA3C\uC2A4\uB97C \uD55C \uB2E8\uACC4 \uB192\uC600\uC2B5\uB2C8\uB2E4. \uD544\uC694\uC5D0 \uB530\uB77C \uC18C\uC2A4 \uCF54\uB4DC\uB294 <code>304 Not Modified</code>\uB85C, \uB514\uD39C\uB358\uC2DC\uB294 <code>Cache-Control: max-age=31536000,immutable</code>\uC744 \uC774\uC6A9\uD574 \uCE90\uC2DC\uB418\uB3C4\uB85D \uD568\uC73C\uB85C\uC368, \uD55C \uBC88\uC758 \uC694\uCCAD\uC774\uB77C\uB3C4 \uB35C \uD558\uB3C4\uB85D \uB9D0\uC774\uC8E0.</p><p>\uC774\uB807\uAC8C\uB098 \uBE60\uB978 Vite\uB97C \uC0AC\uC6A9\uD558\uC9C0 \uC54A\uC744 \uC774\uC720\uAC00 \uC788\uB098\uC694?</p><h2 id="why-bundle-for-production" tabindex="-1">\uBC30\uD3EC \uC2DC \uBC88\uB4E4\uB9C1 \uACFC\uC815\uC774 \uD544\uC694\uD55C \uC774\uC720 <a class="header-anchor" href="#why-bundle-for-production" aria-hidden="true">#</a></h2><p>\uC774\uB807\uAC8C\uB098 \uB2E4\uC591\uD55C \uC774\uC810\uC744 \uAC00\uC9C4 ESM \uC774\uC9C0\uB9CC, \uBE44\uAD50\uC801 \uCD5C\uADFC\uC5D0\uC57C \uC9C0\uC6D0\uB418\uAE30 \uC2DC\uC791\uD588\uAE30\uC5D0 \uC2E4\uC81C \uBC30\uD3EC \uC2DC \uC0AC\uC6A9\uC774 \uBD88\uAC00\uB2A5\uD55C \uBE0C\uB77C\uC6B0\uC800 \uD658\uACBD\uC774 \uC788\uC744 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uB610\uD55C \uB124\uD2B8\uC6CC\uD06C\uB97C \uD1B5\uD574 \uD544\uC694\uD55C \uC2DC\uC810\uC5D0 \uD574\uB2F9 \uBAA8\uB4C8\uC744 \uC694\uCCAD\uD558\uAE30\uC5D0, HTTP/2\uB97C \uC774\uC6A9\uD558\uB354\uB77C\uB3C4 \uC624\uBC84\uD5E4\uB4DC\uAC00 \uBC1C\uC0DD\uB420 \uC218 \uC788\uAD6C\uC694.</p><p>\uB530\uB77C\uC11C, \uBAA8\uB4E0 \uC0AC\uC6A9\uC790 \uACBD\uD5D8\uC744 \uCDA9\uC871\uC2DC\uD0A4\uAE30 \uC704\uD574\uC11C\uB77C\uB3C4 \uBC30\uD3EC \uC2DC\uC5D0\uB294 \uBC88\uB4E4\uB9C1 \uACFC\uC815\uC774 \uD544\uC218\uC801\uC785\uB2C8\uB2E4. \uC774\uAC83\uC774 \uBC14\uB85C vite\uAC00 \uBBF8\uB9AC \uC124\uC815\uB41C <a href="./build.html">\uBE4C\uB4DC \uCEE4\uB9E8\uB4DC</a>\uB97C \uC774\uC6A9\uD558\uACE0, <a href="./features.html#build-optimizations">\uBE4C\uB4DC \uD37C\uD3EC\uBA3C\uC2A4 \uCD5C\uC801\uD654</a>\uB97C \uC9C4\uD589\uD558\uB294 \uC774\uC720\uC785\uB2C8\uB2E4.</p><h2 id="why-not-bundle-with-esbuild" tabindex="-1">\uC65C \uBC88\uB4E4\uB9C1 \uC2DC\uC5D0\uB294 Esbuild\uB97C \uC0AC\uC6A9\uD558\uC9C0 \uC54A\uB098\uC694? <a class="header-anchor" href="#why-not-bundle-with-esbuild" aria-hidden="true">#</a></h2><p><code>Esbuild</code>\uB294 \uAD49\uC7A5\uD788 \uBE60\uB978 \uC18D\uB3C4\uB85C \uBC88\uB4E4\uB9C1\uC774 \uAC00\uB2A5\uD558\uB2E4\uB294 \uC7A5\uC810\uC774 \uC788\uC73C\uB098, \uBC88\uB4E4\uB9C1\uC5D0 \uD544\uC218\uC801\uC73C\uB85C \uC694\uAD6C\uB418\uB294 \uAE30\uB2A5\uC778 \uCF54\uB4DC \uBD84\uD560(Code-splitting) \uBC0F CSS\uC640 \uAD00\uB828\uB41C \uCC98\uB9AC\uAC00 \uC544\uC9C1 \uBBF8\uBE44\uD569\uB2C8\uB2E4. Vite\uC5D0\uC11C \uC0AC\uC6A9\uC911\uC778 Rollup\uC740 \uC774\uC5D0 \uB300\uD574 \uC870\uAE08 \uB354 \uAC80\uC99D\uB418\uC5C8\uACE0 \uC720\uC5F0\uD55C \uCC98\uB9AC\uAC00 \uAC00\uB2A5\uD558\uAC8C\uB054 \uAD6C\uD604\uB418\uC5B4 \uC788\uAE30\uC5D0 \uD604\uC7AC\uB85C\uC11C\uB294 \uC774\uB97C \uC0AC\uC6A9\uD558\uACE0 \uC788\uC73C\uBA70, \uD5A5\uD6C4 <code>Esbuild</code>\uAC00 \uC548\uC815\uD654 \uB418\uC5C8\uC744 \uB54C \uC5B4\uB5A4 \uD504\uB85C\uB355\uC158 \uBC88\uB4E4\uB9C1 \uB3C4\uAD6C\uAC00 \uC801\uC808\uD560 \uAC83\uC778\uC9C0 \uB2E4\uC2DC \uB17C\uC758\uD560 \uC608\uC815\uC785\uB2C8\uB2E4.</p><h2 id="how-is-vite-different-from-x" tabindex="-1">Vite\uC640 \uB2E4\uB978 \uB3C4\uAD6C\uC758 \uCC28\uC774\uC810 <a class="header-anchor" href="#how-is-vite-different-from-x" aria-hidden="true">#</a></h2><p><a href="./comparisons.html">\uB2E4\uB978 \uBE4C\uB4DC \uB3C4\uAD6C\uC640\uC758 \uCC28\uC774\uC810</a> \uC139\uC158\uC5D0\uC11C Vite\uC640 \uB2E4\uB978 \uBC88\uB4E4\uB7EC \uB3C4\uAD6C\uC758 \uCC28\uC774\uC810\uC5D0 \uB300\uD574 \uC790\uC138\uD788 \uB2E4\uB8E8\uACE0 \uC788\uC2B5\uB2C8\uB2E4.</p>',23),d=[s];function p(n,h,c,u,f,b){return r(),t("div",null,d)}var g=e(l,[["render",p]]);export{_ as __pageData,g as default};
